import React, { useState } from 'react'
import NavBar from './component/NavBar';
import Header from './component/Header';
import Menu from './component/Menu';
import Box from './component/box';
import { Data } from "./component/Data";

function App() {
  const [DataMenu, setDataMenu] = useState(Data);

  const newArType = [...new Set(Data.map((D)=> D.type))]

  // Filter by Type
  const filterData = (type) => {
    if (type === 'all') {
      setDataMenu(Data)
    } else {
      const dataAray = Data.filter((Data) => Data.type === type)
      setDataMenu(dataAray)
    }
  }
  return (
    <div >
      <NavBar />
      <div className='container'>
        <Header />
        <Menu filterData={filterData} newArType={newArType} />
        <Box DataMenu={DataMenu} />
      </div>
    </div>
  );
}

export default App;
