import React from "react";
import { Data } from "./Data";
const Menu = ({filterData,newArType})=> {
    
    const onFilter = (type)=> {
        filterData(type)
    }
    return(
        <div>
            {newArType.length ? (Data.map((type)=> {
                return(
                    <div className="bot">
                <button className="bottu" onClick={()=> onFilter({type})}>{type}</button>
    
                <div className="line-bot" ></div>
            </div>
                )
            })) : <h4> ggg </h4>

            }
        </div>
    )
}
export default Menu
